USE nyc_taxi_ldw
GO

CREATE or ALTER procedure silver.usp_silver_trip_data_green
@year VARCHAR(4),@month varchar(2)
AS
BEGIN
DECLARE @create_sql NVARCHAR(MAX),
@drop_sql NVARCHAR(MAX);

set @create_sql='CREATE EXTERNAL TABLE silver.trip_data_green_'+@year+'_'+@month+
 ' WITH (
    LOCATION = ''silver/trip_data_green/year='+@year+'/month='+@month+''',
    DATA_SOURCE = nyc_taxi_src,  
    FILE_FORMAT = parquet_file_format

)
as 
select * from bronze.vw_trip_data_green_csv where year='''+@year+''' and month='''+@month+'''';

EXEC sp_executesql @create_sql;

print(@create_sql)

set @drop_sql='drop EXTERNAL TABLE silver.trip_data_green_'+@year+'_'+@month;

EXEC sp_executesql @drop_sql;
END;

----------------------------------------------------------------------------------------
--- Reading Data from standard JSON file 

---[{"rate_code_id":1,"rate_code":"Standard rate"}
---,{"rate_code_id":2,"rate_code":"JFK"}
---,{"rate_code_id":3,"rate_code":"Newark"}
---,{"rate_code_id":4,"rate_code":"Nassau or Westchester"}
---,{"rate_code_id":5,"rate_code":"Negotiated fare"}
---,{"rate_code_id":6,"rate_code":"Group ride"}
---]
---------------------------------------------------------------------------------------- 

select *
FROM
    OPENROWSET(
        BULK 'rate_code.json',
        DATA_SOURCE='nyc_taxi_data_raw',
        FORMAT = 'CSV',
        FIELDTERMINATOR ='0x0b', 
        FIELDQUOTE = '0x0b',
        ROWTERMINATOR='0x0b'
    ) 
    with (jsondoc NVARCHAR(MAX)) AS [result] 
    CROSS APPLY OPENJSON(jsondoc)
    with (rate_code_id TINYINT,rate_code VARCHAR(25) )
